tinymce.PluginManager.add("jx3icon", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "插入剑三图标",
            body: {
                type: "panel",
                items: [
                    {
                        type: "input",
                        name: "jx3icon",
                        label: `请输入图标ID <a style="color: #f39;font-weight: bold;" href="https://v2.jx3box.com/app/icons/" target="_blank">🌸 点击查看图标大全</a>`,
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();

                // 包裹内容
                let prefix = '<img class="e-jx3-icon" src="https://oss.jx3box.com/icon/'
                let suffix = '.png" />'

                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`${prefix}${data.jx3icon}${suffix}`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("jx3icon", {
        icon : 'jx3icon',
        tooltip: "插入剑三图标",
        // text: '插入视频',
        onAction: function () {
            openDialog();
        },
    });

    // 注册一个菜单项名称 menu/menubar
    // editor.ui.registry.addMenuItem("jx3icon", {
    //     text: "Example菜单名",
    //     onAction: function () {
    //         openDialog();
    //     },
    // });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "jx3icon", //插件名称
                url: "https://github.com/JX3BOX", //作者网址
            };
        },
    };
});
