tinymce.PluginManager.add("macro", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "插入宏",
            size: 'large',
            body: {
                type: "panel",
                items: [
                    {
                        type: "textarea",
                        name: "macro",
                        // label: `..`,
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();

                // 包裹内容
                let prefix = '<pre class="e-jx3macro-area w-jx3macro">'
                let suffix = '</pre>'

                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`${prefix}${data.macro}${suffix}`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("macro", {
        icon : 'macro',
        tooltip: "插入宏",
        // text: '插入视频',
        onAction: function () {
            openDialog();
        },
    });

    // 注册一个菜单项名称 menu/menubar
    // editor.ui.registry.addMenuItem("macro", {
    //     text: "Example菜单名",
    //     onAction: function () {
    //         openDialog();
    //     },
    // });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "macro", //插件名称
                url: "https://github.com/JX3BOX", //作者网址
            };
        },
    };
});
