tinymce.PluginManager.add("pz", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "插入配装方案",
            body: {
                type: "panel",
                items: [
                    {
                        type: "textarea",
                        name: "pz",
                        label: `<p>👘 请填入魔盒配装方案的<a target="_blank" href="/tool/32032">【嵌入版】</a>编码</p>`,
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();

                var pz_string = data.pz
                var pz_re = /<iframe src="(.*?)" .*?><\/iframe>/
                var output = pz_re.exec(pz_string) && pz_re.exec(pz_string)[1]
                var prefix = '<pre class="e-pz-iframe">'
                var suffix = '</pre>'

                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`${prefix}${output}${suffix}`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("pz", {
        icon : 'pz',
        tooltip: "插入配装方案",
        // text: '插入视频',
        onAction: function () {
            openDialog();
        },
    });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "pz", //插件名称
                url: "https://github.com/JX3BOX", //作者网址
            };
        },
    };
});
