tinymce.PluginManager.add("talent2", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "插入镇派方案",
            body: {
                type: "panel",
                items: [
                    {
                        type: "textarea",
                        name: "talent2",
                        label: `<p>💙 请填入<a target="_blank" href="/app/talent2">镇派模拟器</a>生成的编码</p>`,
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();

                // 包裹内容
                let prefix = '<pre class="e-jx3talent2-area">'
                let suffix = '</pre>'

                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`${prefix}${data.talent2}${suffix}`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("talent2", {
        icon : 'talent2',
        tooltip: "插入镇派方案",
        // text: '插入视频',
        onAction: function () {
            openDialog();
        },
    });

    // 注册一个菜单项名称 menu/menubar
    // editor.ui.registry.addMenuItem("talent2", {
    //     text: "Example菜单名",
    //     onAction: function () {
    //         openDialog();
    //     },
    // });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "talent2", //插件名称
                url: "https://github.com/JX3BOX", //作者网址
            };
        },
    };
});
